$(document).ready(function(){

txtLink = $("table.bibLinks td").html();
httpAyir = txtLink.split("\"");
linkAyir = httpAyir[1].split("/");

//CRC için özel Link oluşturma
if(linkAyir[2].indexOf("crcnetbase")>0)
{
linkAyir[2] = linkAyir[2].replace("marc","www")
}

if(linkAyir[2].indexOf("hdl.handle.net")<0)
{
offKampLink = "http://0-"+linkAyir[2]+".divit.library.itu.edu.tr/"

for(i=3;i<linkAyir.length; i++){
	offKampLink+=linkAyir[i];
	if(typeof linkAyir[i+1] !== 'undefined')
		{
		offKampLink+="/";
		}
}

//pencere = " onclick="+"\"window.open('"+offKampLink+ "'); return false;\">"

fullLink = "<a href=\""+offKampLink+"\">Off-Campus Connection</a>"
$("table.bibLinks tr a").removeAttr('onclick');

$("table.bibLinks tr:last").after('<tr align="center"><td>'+fullLink+'</td></tr>');

}





});





